import React, { useState, useEffect } from "react";
import { Layout, Row, Col, Image, Carousel } from "antd";
import AppHeader from "../../../../components/Header";
import AppFooter from "../../../../components/Footer";
import AppComment from "../comment";
import AppDescribe from "../describe";
import AppUserinfo from "../userinfo";
import AppState from "../state";
import "./../../index.scss";
import roomInfoApi from "../../../../api/roomInfoApi";

const { Header, Content, Footer } = Layout;

function DetailPage() {
  const queryString = window.location.search;
  const urlParams = new URLSearchParams(queryString);
  const [arrUrl, setArrUrl] = useState([]);
  const [data, setData] = useState([]);
  useEffect(() => {
    const roomId = urlParams.get("room-id");
    roomInfoApi.getInfoRoom(roomId).then((res) => {
      setArrUrl(res.files);
      setData(res);
    });
  }, []);
  return (
    <Layout className="mainLayout">
      <Header>
        <AppHeader />
      </Header>
      <Content>
        <div className="main">
          <div id="news" className="block featureBlock bgGray">
            <div className="container-fluid">
              <div className="titleHolder top_margin">
                <h2>Chi tiết phòng trọ</h2>
              </div>
              <Row gutter={[16, 16]} className="carousel">
                <Col xs={{ span: 14 }} sm={{ span: 14 }} md={{ span: 14 }}>
                  <Carousel>
                    {arrUrl.map((e) => {
                      return (
                        <Image
                          width={500}
                          height={500}
                          src={e.url}
                          fallback="data:image/png;base64,"
                        />
                      );
                    })}
                  </Carousel>
                </Col>
              </Row>
              <div style={{paddingLeft: 100, paddingRight: 100, paddingTop: 20}}>
                <p>
                  <span style={{ color: "red", fontWeight: "bold" }}>
                    {data.title}
                  </span>
                  <br />
                  <span>Địa chỉ: {data.address}</span>
                  <br />
                  <span>Địa chỉ: {data.roomPrice} triệu/tháng</span>
                  <br />
                  <span>
                    Diện tích: {data.acreage} m<sup>2</sup>
                  </span>
                  <br />
                  <span>Ngày đăng: {data.date}</span>
                </p>
                <p>
                  <b>Thông tin mô tả</b>
                </p>
                {data.roomDescribe}
              </div>
              <Row gutter={[16, 16]}>
                <Col xs={{ span: 24 }} sm={{ span: 24 }} md={{ span: 22 }}>
                  {/* <AppUserinfo/> */}
                  {/* <AppState/> */}
                </Col>
              </Row>
              <Row gutter={[16, 16]}>
                <Col xs={{ span: 24 }} sm={{ span: 24 }} md={{ span: 22 }}>
                  <AppComment />
                </Col>
              </Row>
            </div>
          </div>
        </div>
      </Content>
      <Footer>
        <AppFooter />
      </Footer>
    </Layout>
  );
}

export default DetailPage;
